mapper.select:
SELECT MAX(`CALL_DATE`) AS maxCallOutDate,MIN(`CALL_DATE`) AS minCallOutDate
FROM T_CALL_INFOWHERE `USER_INFO_ID`=#{USERINFOID,JDBCTYPE=BIGINT}
AND `CALL_TYPE`='主叫'

